void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float i,j,d=.2,e=0.; vec3 p;
fragColor=vec4(0);
for (i=0.;i<64.;i++){
    if (d<=1e-4) break;
    p=vec3((fragCoord.xy-.5*iResolution.xy)/iResolution.y,1)*e;
    p*=vec3(length(p),p.z-256.-mod(iTime*9.,96.),atan(p.y,p.x));
    for ( j=0.;j<7.;j++)
        p=.8-abs(abs(p)),
        d=max(max(cos(p.y),sin(p.x)),tan(p.z)),
        d-=dot(p,p)/2e4;
    e+=d;
}fragColor+=vec4(2,4,3,1)*sin(j)/i;
}
