void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 pos = vec2(fragCoord.x/iResolution.x*4.0-2.0, fragCoord.y/iResolution.y*4.0-2.0);
    fragCoord = vec2(iMouse.x/iResolution.x*4.0-2.0, iMouse.y/iResolution.y*4.0-2.0);
    float it = 0.0, limit = iTime*5.0;
    while((it += 0.05) < limit){
        float x = pos.x * pos.x - pos.y * pos.y;
        pos.y = 2.0 * pos.x * pos.y + fragCoord.y;
        pos.x = x + fragCoord.x;
        if(pos.x * pos.x + pos.y * pos.y > 4.0) break;
    }

    // Output to screen
    fragColor = it >= limit ? vec4(0.0,0.0,0.0,1.0) : vec4(0.5+0.5*sin(it),0.0,1.0,1.0);
}
