#define PI 3.14159265359
float sdfCircle(vec2 p, float r) {
    return length(p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = ((fragCoord/iResolution.xy) - vec2(0.5,0.5)) * 2.;
    p.x *= iResolution.x / iResolution.y;
    float aa = 0.005;
    float loopingTime = fract(iTime)*PI*2.;

    float updownscale = 0.6;
    float updowndelay = ((sin(iTime/2.)+1.)/2.)*1.5; // jiggle factor

    float width = pow(abs(p.x),2.)*2.;

    float circleHeight = 0. + sin(loopingTime)*updownscale;
    float circleHeightDelay = 0. + sin(loopingTime-updowndelay)*updownscale;
    float circleHeightJiggle = 0. + sin(loopingTime-width*updowndelay)*updownscale;

    // draw guidance circles
    float guidanceCircleMask = 0.;

    vec2 circle1Space = p - vec2(-0.7,circleHeight);
    guidanceCircleMask = smoothstep(0.+aa, 0.-aa, sdfCircle(circle1Space, 0.05));

    vec2 circle2Space = p - vec2(0.7,circleHeightDelay);
    guidanceCircleMask += smoothstep(0.+aa, 0.-aa, sdfCircle(circle2Space, 0.05));

    // draw plump circle
    float plumpCircleMask = 0.;
    vec2 circle3Space = p - vec2(0.,circleHeightJiggle);
    guidanceCircleMask += smoothstep(0.+aa, 0.-aa, sdfCircle(circle3Space, 0.4));


    float v = guidanceCircleMask + plumpCircleMask;
    //v = width;
    fragColor = vec4(v,v,v,1.0);
}
