float triangle_wave(float x, float freq, float amp) {
  return abs(mod(x * freq, amp));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float offset = triangle_wave(iTime,0.5,1.);

    if(uv.y>offset &&uv.y<0.12+offset){
        uv.x+=tan(fragCoord.y-mod(50.*iTime,iResolution.y))*0.1;
    }
    // Hint, it's a few lines of code, and makes use of a tan function :)
    vec2 warpedUv = uv;

    vec2 offset1 = vec2(0.,sin(iTime)*0.02);
    vec2 offset2 = vec2(0.02,0.);
    vec2 offset3 = vec2(0.04,-sin(iTime)*0.02);

    float r = texture(iChannel0, warpedUv + offset1).r;
    float g = texture(iChannel0, warpedUv + offset2).g;
    float b = texture(iChannel0, warpedUv + offset3).b;



    vec3 col = vec3(r, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
