void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.89,0.89,0.9); // bg color
    vec2 target = vec2(0.5, 0.); // where we want to draw the bullseye

    float t = iTime*2.;

    uv.x += (sin(uv.y*5.2+iTime)*.2);

    float lengthOffset = sin(uv.x*5.+(t*2.))/10. - cos(uv.y-(t*3.))/120. - sin(uv.x*4.-t)/12.;

    float l = length(uv - target) + (lengthOffset-(uv.y*.2));

    if (l < 0.8 && mod(l, 0.04) < .005) {
        col = vec3(.5,0.,0.);
    } 

    fragColor = vec4(col,1.0);
}
