#ifdef GL_ES
precision mediump float;
#endif

vec3 colorA = vec3(0.5, 0.1, 0.8);
vec3 colorB = vec3(0.8, 0.5, 0.1);
float PI = 3.14159265359;

float circle(in vec2 coord, in float radius, in float lineWidth)
{
    float dist = length(coord);
    return smoothstep(radius + lineWidth, radius - lineWidth, dist);
}

void mainImage ( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = gl_FragCoord.xy / iResolution.xy;
    vec2 center = vec2(0.5, 0.5);
    vec2 coord = uv - center;

    float angle = atan(coord.y, coord.x);
    float distance = length(coord) * 2.0;

    float t = iTime * 0.5;
    float freq = 4.0;
    float radius = 0.2 + 0.15 * sin(freq * angle + t);

    float lineWidth = 0.01 + 0.005 * sin(3.0 * distance - t);
    float spiral = circle(coord, radius, lineWidth);

    vec3 col = mix(colorA, colorB, (0.5 * sin(t) + 0.5));
    col = mix(col, vec3(0.0), spiral);

    fragColor = vec4(col, 1.0);
}
