//Buffer A runs physics calculations and stores velocity and position vetors

//Duplicated func in Buffer A and Image module
vec4 objectData(int objectIndex) {
    return texelFetch(iChannel0, ivec2(objectIndex, 0), 0);
}


float random(float seed) {
    return fract(sin(seed*3123.43758287) * 43758.5453123);
}

vec2 randomPos(float seed) {
    return vec2(random(random(seed))*iResolution.x/iResolution.y, random(random(random(seed))));
}

float smoothStepBoundries(float x,  float maxBoundry) {
    return 1.-smoothstep(0., BOUNDRY_WIDTH, x) -smoothstep(maxBoundry-BOUNDRY_WIDTH, maxBoundry, x);
}

void mainImage( out vec4 posVelData, vec2 uv ) {

    //each pixel along x will physics update an object
    int objectIndex = int(floor(uv.x));
    if(uv.y > 0.5 || objectIndex > NUM_OBJECTS) discard;


    //init ===================================
    if (iFrame < 5) {
        //posVelData = vec4(randomPos(float(objectIndex)), normalize(randomPos(42560234.1235*float(objectIndex))));
        posVelData = vec4(randomPos(float(objectIndex)), 0., -1.);
    } else {
    //phys update ===================================
        vec4 objectData = objectData(objectIndex);
        vec2 pos = objectData.xy;
        vec2 vel = objectData.zw;

    //sum forces ===================================        
        vec2 force = vec2(0.);

        //add boundry forces
        force += BOUNDRY_FORCE*vec2(smoothStepBoundries(pos.x, iResolution.x/iResolution.y), smoothStepBoundries(pos.y, 1.));

        //add mouse effects
        vec4 m = iMouse / iResolution.y;
        // button is down
        if( m.z>0.0 ) {
            //TODO: add falloff?
            force += MOUSE_FORCE*normalize(pos-m.xy)/dot(pos-m.xy, pos-m.xy);
        }


    //step update ===================================  
        vel += force*DELTA_T;
        vel = clamp(vel, -MAX_VEL, MAX_VEL);
        pos = pos+vel*DELTA_T;

        //store data
        posVelData = vec4(pos, vel);
    }
}

#define NUM_OBJECTS 30

#define DELTA_T .01
#define MAX_VEL 0.75

#define BOUNDRY_WIDTH 0.1
#define BOUNDRY_FORCE 20.0
#define MOUSE_FORCE 0.1

#define PI 3.1415926538

// Smooth HSV to RGB conversion 
// Source https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

//Buffer B draws objecs and creates fading trails

//Duplicated func in Buffer A and Buffer B module
vec4 objectData(int objectIndex) {
    return texelFetch(iChannel0, ivec2(objectIndex, 0), 0);
}

float circle(vec2 p, float r, vec2 uv)
{
    return smoothstep(1.5/iResolution.y, 0., length(uv-p)-r);
}

vec3 objColor(int objIndex) {
    return hsv2rgb_smooth(vec3(float(objIndex)/float(NUM_OBJECTS), 0.7, 1.));
}

void mainImage( out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.);

    for(int i=0; i<NUM_OBJECTS;i++)
    {
        vec4 objectData = objectData(i);
        vec2 pos = objectData.xy;
        vec2 vel = objectData.zw;

        col += objColor(i)*circle(pos, .02, uv);
    }


    // Output to updated Buffer B
    vec4 tracePaths = texture(iChannel1,uv);
    col += tracePaths.xyz*.9;

    fragColor = vec4(col, 1.0);
}
