void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Calculate the scroll amounts
    float scrollX = iTime * 0.05;
    float scrollY = iTime * 0.4;

    // Calculate the scaled and rotated UV coordinates
    float scaleX = (iMouse.x > 0.0) ? ((iMouse.x / iResolution.x) * 10.0) : 1.0;
    float scaleY = (iMouse.y > 0.0) ? ((iMouse.y / iResolution.y) * 10.0) : 1.0;

    float scrollVelocity = 0.25;
    float angle = iTime * scrollVelocity;
    vec2 scaledUV = uv * vec2(scaleX, scaleY);
    vec2 rotatedUV = vec2(cos(angle) * scaledUV.x - sin(angle) * scaledUV.y,
                           sin(angle) * scaledUV.x + cos(angle) * scaledUV.y);

    // Calculate the x and y coordinates with infinite tiling and scrolling
    float x = mod(rotatedUV.x + scrollX, 2.0);
    if (x > 1.0) {
        x = 2.0 - x;
    }
    float y = mod(rotatedUV.y + scrollY, 2.0);
    if (y > 1.0) {
        y = 2.0 - y;
    }

    // Sample the texture at the transformed UV coordinates
    fragColor = texture(iChannel0, vec2(x, y));
}
