void mainImage( out vec4 O, vec2 U ){

    vec2 uv = U/iResolution.xy;

    uv.y = 1.0-uv.y;

    O = mix(vec4(1, 0, 1, 1), vec4(0, .8, 1, 1), smoothstep(0., 1., uv.x))*.9;

    float t = mod(iTime*.2,1.);
    O *= uv.y<.03 && mod(iTime, 2.) < 1.8?1.:1./(uv.y*(mod(iTime, 2.)>1.8?10.:60.));
    O *= vec4(uv.x < t);
    O *= uv.y<.03?1.:uv.x+.1 < t?1.:sqrt(1.-pow((-uv.x + t)*10.-1., 2.));

}
