void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    float w = length(fwidth(uv)) / 2.;

    float ball = length(uv - vec2(0., sin(iTime * 2.) * .2 + .2)) - .2;
    float rect = uv.y;

    const float k = .1;
    float h = clamp(.5 - .5 * (rect + ball) / k, 0., 1.);
    float d = mix(rect, -ball, h) + k * h * (1. - h);
    d = min(ball, d + .072);

    vec3 col = vec3(smoothstep(-w, w, d));

    fragColor = vec4(col, 1.);
}
