void mainImage( out vec4 O, vec2 U ) {

    bool esq = int(U.y)%2==0; 

    vec2  A;
    vec3  R = iResolution, P;
          U = ( U+U - R.xy ) / R.y;                                // normalize coordinates

    float s = .1761,                                              // s+s = 2Pi/72 = 5Â° steps
          z = 1. - dot(U,U),
          paralax = .1*(esq?-1.:+1.)
        ;


    //U.x += paralax; 


    // 3D point on front half-sphere 
    P = vec3( U, sqrt(z) ), 



    P.x += paralax,
    // rotate on sphere
    P.yz *= r,
    P.xz *= r, 





    A = vec2( atan(length(P.xy),P.z), atan(P.x,P.y) ),        // get parameterization


    // bg color is black
    O *= 0.; 
    //O = vec4(.5,.5,0., 0.); 

    // if on sphere
    //z > 0.                                                         
    //  ? 

        O.g = 1. - length( ( mod( A+s, s+s ) -s) 
                       // / fwidth(A) ) * 50./R.y               
                       * inverse(transpose(mat2(dFdx(A),dFdy(A)))) )  * 1e1/R.y  // ( precise )
                       //* inverse(transpose(mat2(dFdx(A),dFdy(A)))) )  * 1e2/R.y  // ( precise )
                       //* 1e4/R.y)
      //: s;
      ;
}
