void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;

    float offsetX = 100.0f;
    float offsetY = 100.0f;
    float offsetZ = sin(iTime / 4.0f) * 1.5f;


    if (fragCoord.x < ((iResolution.x * 0.25f + offsetX) / offsetZ) || fragCoord.x > ((iResolution.x * 0.75f + offsetX) / offsetZ)  || fragCoord.y < ((iResolution.y * 0.25f + offsetY) / offsetZ) || fragCoord.y > ((iResolution.y * 0.75f + offsetY) / offsetZ))
    {
        col = vec3(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y, 0.0f);
    }
    else
    {
        col = vec3(0.5f, 0.5f, 1.0f);
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
