void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 center = vec2(0.5, 0.5);
    float dist = length(uv - center);

    vec3 color = vec3(0.4); 

    if(dist < 0.3) {
        color = vec3(1.0, 0.75, 0.70); 
    }

    float leftEye = length(uv - (center + vec2(-0.1, 0.1)));
    float rightEye = length(uv - (center + vec2(0.1, 0.1)));
    float mouth = length(uv - (center + vec2(0, -0.1)));

    if(leftEye < 0.05 || rightEye < 0.05) {
        color = vec3(0.4, 0.3, 0.2); 
    }

    if(mouth < 0.05) {
        color = vec3(0.8, 0.0, 0.0);
    }

    if(uv.y+0.14 > center.y && dist < 0.42 && dist > 0.20) {
        color = vec3(0.4, 0.2, 0.0); 
    }

    fragColor = vec4(color, 1.0);
}
