vec3 palette(float t, vec3 a, vec3 b, vec3 c, vec3 d){
    return a + b*cos(6.28318*(c*t*d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalCol = vec3(0.0); //color black

    for (float i = 0.0; i < 4.0; i++) { //more iterations makes it more random
        uv = fract(uv * 1.5) - 0.5; //multiplying the uv by an inexact number makes it more random

        float d = length(uv) * exp(-length(uv0));

        vec3 v1 = vec3(0.5,0.5,0.5);
        vec3 v2 = vec3(0.5,0.5,0.5);
        vec3 v3 = vec3(1.0,1.0,1.0);
        vec3 v4 = vec3(0.263,0.416,0.557);
        vec3 col = palette(length(uv0) + i*.4 + iTime,v1,v2,v3,v4);

        d = sin(d*8. + iTime);
        d = abs(d);
        d = 0.1/d;

        d = pow(d, 1.2);

        finalCol += col * d;
    }

    // Output to screen
    fragColor = vec4(finalCol,1.0);
}
