void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 n1 =  texture(iChannel0,uv*2.0 - 0.01 * iTime);
    vec4 n2 =  texture(iChannel0,uv*0.25 - iTime * 0.017);
    vec4 n3 =  texture(iChannel0,uv*1.1 + iTime * 0.02);
    // Output to screen

   fragColor = 1.0 - (n3 * n2 * n1 );
}
