#define PI 3.14159265
#define rot2D(a) mat2(sin(a),cos(a),-cos(a),sin(a))

float w(float x,float a,float s,float offset){
    float S = pow(a,s);
    return sin(x*S+offset)/S;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float c = 0.;
    float repeat = 6.;
    float s = 10.;// scale factor
    float t = iTime;
    vec2 p = uv*2.;


    for(float i = 0.;i < repeat;i++){
        c += w(p.x+w(p.y+w(p.x,s,i,t*i),s,i,t-i),s,i,t*i*2.);
        p = rot2D(PI/7.)*p;
        p+=cos(c);
        p.x = p.x + c;
        p.y = p.y - c;
        p += uv.xy*rot2D(PI/(i+.1));
    }

    //c = w(uv.x,s,3.,iTime)+.5;
    c = c*.2+.5;
    vec3 col = mix(vec3(.6,.9,1),vec3(0,.2,1),c);

    fragColor = vec4(col,1.0);
}
