mat2 rot(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

vec3 col;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;


    uv -= vec2(0.5,0.5);
    uv *= rot(iTime*0.75);
    uv.y /= cos(iResolution.y/iResolution.x);

    uv += vec2(0.25);
    if(uv.x>0.0 && uv.y>0.0 && uv.y<0.5 && uv.x<0.5) {

      col = texture(iChannel1,uv*2.0,1.0).rgb;
      col = col*col*8.0;

    }



    float n = floor(uv.y*8.0);
    if(mod(n,2.0)==0.0) {
        col +=vec3( step(fract(uv.x*5.0),0.5) );
        col.b *= 1.5;
    } else {
        col +=vec3( step(fract(0.5+uv.x*5.0),0.5) );
        col.r *=1.5;
    }

    col *= 0.25;

    col = mix( texture(iChannel0,uv,1.0).rgb, col, 0.5);


    fragColor = vec4(col,1.0);
}
