#define PI 3.141592

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy * 2.0 / iResolution.xy - 1.0;

    float d = length(uv);
    //d = max(abs(uv.x), abs(uv.y)); //linear uv interpolation variant

    d = mix(d, 1.0, sin(iTime * 1.0 * PI) * 0.5 + 0.5);
    uv = uv / d;
    uv = uv * 0.5 + vec2(0.5);

    //sync blending with pinch pulsation
    //this monstruosity was quickly hacked in graphtoy and 100% unoptimized
    float p2 = abs(fract((iTime + 1.5) * 0.25) - 0.5) * 8.0 - 2.0;
    p2 = smoothstep(0.0, 1.0, clamp(p2, -1.0, 1.0) * 0.5 + 0.5);

    vec3 col = mix(texture(iChannel0, uv), texture(iChannel1, uv), p2).rgb;
    fragColor = vec4(col, 1.0);
}
