#define r(a) mat2( cos(a), -sin(a), sin(a), cos(a) )
#define f(z) (inverse(z) - .7*r(iTime+5.)*z)

void mainImage( out vec4 C, vec2 U )
{
    vec2 R = iResolution.xy, u = ( U+U - R ) / R.y;

    u = f(f(f(f(f(mat2(u, -u.y, u.x))))))[0]; 

    u.y = 2.5 + 2.*sin(u.y*12.);
    u += atan(u.x, 5.*u.y);


    float c = .7 + .7*sin(exp(-u.x*u.x*.2)*11.) - smoothstep(0., 28., dot(u, u));

    C = vec4(c, c-.1, c-.2,1);
}
