vec4 drawCircle(vec2 center, float radius, vec4 color, in vec2 fragCoord, in vec4 fragColor)
{
    // Calculate aspect ratio of the viewport
    float aspectRatio = iResolution.x / iResolution.y;

    // Normalize fragCoord
    vec2 normalizedCoord = fragCoord / iResolution.xy;
    normalizedCoord.x *= aspectRatio;

    // Adjust center for the aspect ratio
    center.x *= aspectRatio;

    // Distance check
    if (distance(normalizedCoord, center) <= radius)
    {
        return color; // Inside the circle
    }
    return fragColor;

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Set background color to black
    fragColor = vec4(0.55, 0.55, 0.55, 1);

    // Circle parameters
    float circleRadius = 0.25;
    vec2 circleCenter = vec2(0.5, 0.5); // Center the circle within a square
    vec4 circleColor = vec4(1, 0, 0, 1);

    // Draw sclera
    vec4 scleraColor = vec4(1.0);
    fragColor = drawCircle(circleCenter, circleRadius, scleraColor, fragCoord, fragColor);

    // Draw iris
    vec4 irisColor = vec4(0.0, 0.0, 1.0, 1.0);
    float irisRadius = 0.075;
    vec2 pupilPosition = vec2(0.5);
    float pupilSqueeze = 0.0;
    float pupilRadius = 0.1;
    fragColor = drawCircle(pupilPosition, pupilRadius + irisRadius, irisColor, fragCoord, fragColor);

    // Draw pupil
    vec4 pupilColor = vec4(0,0,0,1);     
    fragColor = drawCircle(pupilPosition, pupilRadius, pupilColor, fragCoord, fragColor);    

    // Draw glare
    float glareRadius = 0.06;
    fragColor = drawCircle(pupilPosition+0.04, glareRadius, vec4(1), fragCoord, fragColor);    
}
