void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 finalColor = vec3(0.,0.,0.);
    vec2 uv=(fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0=uv;



    for(float i=0.;i<8.;i++){





        uv*=1.5;
        uv=fract(uv);
        uv-=0.5;
        uv*=smoothstep(-3.,.6,sin(iTime/2.))*1.4;


        float d = length(uv)*exp(-length(uv0));

        vec3 color = vec3 ((uv.x-uv.y)*d*sin(d*18.+iTime*4.)/d/sin(iTime) , ((uv.x-uv.y)*d*sin(d*19.+iTime*3.5)/d/sin(iTime)+(-uv.x+uv.y)*d*sin(d*30.+iTime*2.2)/d/cos(iTime))/2. , (-uv.x+uv.y)*d*sin(d*35.+iTime*2.)/d/cos(iTime));
        color *= exp(-length(uv0));

        finalColor += color;

        fragColor = vec4(finalColor,1.);

    }

    }
