float dot2( in vec2 v ) { return dot(v,v); }

float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
    {
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    }

    return sqrt(min(dot2(p-vec2(0.00,1.00)), dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

vec3 pallet(float t)
{
    vec3 a = vec3(0.8,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1,1,1);
    vec3 d = vec3(0.263,0.414,0.525);
     return a+b*cos(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;

    uv.y += 0.5;
//    float d = sdHeart(uv)-0.25;
//    d = sin(d*10.+iTime)/10.;
//    d = abs(d);
    //d = smoothstep(0.0, 0.1, d);
//    d = 0.02/d;

    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < 5.0; i ++)
    {
        uv *= 3.0;
        uv = fract(uv);
        uv.x -= 0.5;
        uv.y += 0.2;

        float d = sdHeart(uv)*exp(-.5*sdHeart(uv0));
        vec3 col = pallet(sdHeart(uv0) + i*4. + iTime*.6);

        d = sin(d*8.+iTime)/8.;
        d = abs(d);

        d = pow(0.01/d,1.25);

        finalColor += col*d;
    }


    fragColor = vec4(finalColor,1.0);
}
