float oscillate(float minVal, float maxVal, float speed) {
    float t = 0.5 + 0.5 * sin(iTime * speed); // Oscillates between 0 and 1
    return mix(minVal, maxVal, t); // Linearly interpolates between minVal and maxVal based on t
}

vec3 pal( in float t ){
    //vec3 a = vec3(0.448, 0.548, 0.488);
    //vec3 b = vec3(0.350, 0.475, 0.475);
    //vec3 c = vec3(0.320, 0.320, 0.320);
    //vec3 d = vec3(0.288, 0.622, 0.955);
    float scalar = 0.0234;
    float scalarB = 0.0155;
    vec3 a = vec3(0.448, 0.548, 0.488);
    vec3 b = vec3(0.350, 0.475, 0.475);
    vec3 c = vec3(scalarB, scalarB, scalarB);
    vec3 d = vec3(0.428 + scalar, 0.762 + scalar, 1.095 + scalar);

    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < 4.1891; i++){

        uv = fract(uv * 1.5) - 0.5;

        //float tiempo = oscillate(0.2, 0.6, 1.0); // Adjust the speed as needed

        //vec3 col = vec3(0.1 + tiempo, tiempo + 0.2, 0.6 + tiempo);

        //float d = length(uv);
        float d = length(uv) * exp(-length(uv0));

        vec3 col = pal( length(uv0) + i*0.4 + iTime*0.003);

        d = sin( (d * 8.0 + iTime))/0.653;
        d = abs(d);
        //d = step(0.1, d);
        //d = smoothstep(0.0, 0.1, d);
        //d = 0.0933369 / d;
        d = pow(0.0933369 / d, 1.2);
        //col *= d;
        finalColor += col * d;
    }


    fragColor = vec4(finalColor, 0.1 );
}
