vec3 color(in float t) {
    vec3 a = vec3(3.138, 0.5, 0.5);
    vec3 b = vec3(0.500, 0.500, 0.5);
    vec3 c = vec3(1., 1.138, 1.);
    vec3 d = vec3(0., -0.342, 0.558);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    vec2 globaluv = uv;

    vec3 finalColor = vec3(0.,0.,0.);
    vec3 col = vec3(0.);
    float intensity = 0.;

    for(float i = 0.; i < 2.; i++){
        uv = (fract(uv*1.5)-.5);   

        intensity = length(uv) * cos(length(globaluv)*3.14/2.);
        col = color(length(globaluv) + iTime*1.2 + i);

        intensity = sin(intensity*4. + iTime);
        intensity = abs(intensity);
        intensity = pow(0.03/intensity, 1.5);

        finalColor += col * intensity;
    }

    fragColor = vec4(finalColor,1.0);
}
