// [FLASHING, RAPID WARNING]

#define pi 3.14

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = 2.0f * uv - 1.0f;
    uv *= 2.0f;

    vec2 texres = iChannelResolution[0].xy;

    vec2 x = vec2(-texres.x, texres.x);
    vec2 y = vec2(-texres.y, texres.y);
    float dx = x.y - x.x;
    float dy = y.y - y.x;

    vec4 n = vec4(x.x + cos(uv.x*2.0*pi) * dx/((iTime)*pi),
                  y.x + sin(uv.y*2.0*pi) * dy/((iTime)*pi),
                  x.x + cos(uv.x*2.0*pi) * dx/((iTime)*pi),
                  y.x + sin(uv.y*2.0*pi) * dy/((iTime)*pi));

    n = normalize(n + float(iFrame) * iTime + iTime);

    vec4 t = vec4(texture(iChannel0, n.xy).r,
                  texture(iChannel0, n.yz).r,
                  texture(iChannel0, n.zy).r,
                  texture(iChannel0, n.yx).r);

    fragColor = vec4(mix(mix(t.x, t.y, 0.5f),
                         mix(t.z, t.w, 0.5f), 0.5f));
    fragColor.rgb *= fragColor.a;

}
