#define res iResolution
#define time iTime
void mainImage( out vec4 ret, in vec2 fc ){
    vec4 bb= texture(iChannel0,fc/res.xy);
    int i= iFrame;
    if(i<=1){
        ret= vec4(1.);
        return;
    }

    ivec2 w= ivec2(res);

    fc/=2.;
    w/=2;
    if(bb.rgb==BLUE){
        ret= vec4(GREEN,1.);
        return;
    }
    else if(bb.rgb!=WHITE){
        ret= bb*.975;
        if(ret.a<.1)
            ret= vec4(0.);
        return;
    }

    ivec2 p= ivec2(fc.xy);
    int s= p.x + p.y*w.x;

    ret= (s==i)||((s%i)!=0) ? vec4(bb):vec4(BLUE,1.);
}

