// variant of https://shadertoy.com/view/mdXyDH


#define rot(a)      mat2(cos(a+vec4(0,11,33,0)))                    // rotation
//#define L(q)      max( abs(q.z)-1., length(abs(q.xy)-1.) ) - .1   // cube
#define L(q)        max( q.z, length(q.xy) ) - .1                   // tripod ( or cube, with  q = abs(q) - 1. )

void mainImage(out vec4 O, vec2 U)
{
    float t=9.,r,i;
    vec3  R = iResolution,
          D = normalize(vec3(U+U, -R.y) - R),                       // ray direction
          p = vec3(0,0,3), q, P,                                    // marching point along ray 
          C = iMouse.z > -0. ? 6.* iMouse.xyz/R -3.                 // camera control
                             : 3.* cos(.3*iTime + vec3(0,11,0));

    for ( O=vec4(1); O.x > 0. && t > .01; O-=.01 ) {                // march scene
        P = p, 
        P.yz *= rot(-C.y),                                          // rotations
        P.xz *= rot(-C.x);
        for ( i=0.; i++ < 5. ; P.xy *= rot(1.26) )                  // 2pi/5 rotational symmetry
            q = P,
            q.x-=.7, q.xz *= rot(.4), q.yz*=rot(.4),                // offset and tilt
            q = abs(q) - 1.,
            t = min( min(t,L(q)), min(L(q.yzx),L(q.zxy)) );         // 3 ortho cylinders

        p += .3*t*D;                                                // step forward = dist to obj  
    }

   O *= O*O*O*2.;                                                   // color scheme 
}
