void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 fnord = fragCoord / iResolution.xy * 2.0 - 1.0;
    float aspect_ratio = iResolution.x / iResolution.y;


    fnord.x *= aspect_ratio;

    vec3 color = vec3(0.0);
    vec3 shade = vec3(0.0);

    for (float i = 0.0; i < 7.0; ++i) {

        float r = smoothstep(0.1, 0.5, 1.0 - distance(fnord + sin(iTime / 2.0), vec2(sin(iTime + 5.0 + i * 2.0), sin(iTime + 55.0 + i * 3.0))));
        float g = smoothstep(0.1, 0.2, 1.0 - distance(fnord + cos(iTime / 3.0), vec2(sin(iTime + 12.0 + i * 4.0), sin(iTime + 8.0 + i * 11.0))));
        float b = smoothstep(0.01, 0.5, 1.0 - distance(fnord + sin(iTime / 6.0), vec2(sin(iTime + 25.0 + i * 5.0), sin(iTime + 11.0 + i * 23.0))));

        color += (vec3(pow(r, 3.0), pow(g, 5.0), pow(b, 2.0)));

        shade = vec3(distance(color.rg, fnord.xy));


    }


    fragColor = vec4(1.0 - ((color / 2.0) + shade), 1.0);

}
