float circle(vec2 p, float r, float n)
{
    float s = pow(abs(p.x), float(n)) + pow(abs(p.y), float(n));
    float L = pow(s, 1.0 / float(n));
    return L - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord/iResolution.xy - .5) * 2.;
    float ar = iResolution.x / iResolution.y;
    vec2 p = uv * vec2(ar, 1.0);
    float sd = circle(p, 0.7, iTime/3.0);
    float aa = 1. - clamp(sd / fwidth(sd) + .5, 0., 1.);
    fragColor = vec4(vec3(aa), 1.0);
}
