#define PI 3.1415926535897932384626433832795
#define r1 200
#define r2 250
#define zoom 1.5
#define width 10.
float func(float x){
    return sin(x/50.)*100.;
}
float diff(vec2 pos){
    return abs(pos.y - func(pos.x));
}
float mouseDown(){
    return step(0., iMouse.z);
}
float warpFunc(float len){
    return float(zoom)+smoothstep(float(r1), float(r2), len/mouseDown())*(1.-float(zoom));//1. + cos(PI*min(len/float(r), 1.)/2.)*(float(zoom) - 1.)*mouseDown();
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 pos = ((fragCoord-0.5*iResolution.xy)/iResolution.y)*2.;
    //vec2 mousePos = ((iMouse.xy-0.5*iResolution.xy)/iResolution.y)*2.;
    vec2 pos = fragCoord-iResolution.xy/2.;
    vec2 mousePos = iMouse.xy - iResolution.xy/2.;
    float len = length(pos - mousePos);
    pos = mousePos + (pos - mousePos)/warpFunc(len);//(mousePos - pos)*20.*;

    vec3 col = vec3(0);

    //col += texture(iChannel0, pos.xy/500.).rgb;

    col += smoothstep(width, 0., diff(pos));

    col.b += smoothstep(0.9, 1., abs(mod(pos.x/100., 1.)*2.-1.))*smoothstep(float(r2), float(r1), len/mouseDown());
    col.b += smoothstep(0.9, 1., abs(mod(pos.y/100., 1.)*2.-1.))*smoothstep(float(r2), float(r1), len/mouseDown());


    fragColor = vec4(col,1.0);
}
