void mainImage(out vec4 O, vec2 I)
{
    I = ceil(I/iResolution.y*40.)-vec2(37,22);
    float v = I.y,
    h = dot(I,I),
    z;
    O = vec4(240,0,300,0)+I.x-4.*v;
    if (h<2e2)
    {
        O = vec4(420,0,520,0);
        I *= 50.+.08*h;
        z = (I.x-I-I).y/1e2+18.;
        z>0.?O.r += z*z : z;
        O = O*(I.y+11e2)/5e3 + max(I.x+I.y,0.)*.1;
    }
    else if (v<0.)
    {
        O.r = 150.+v+v;
        O.b = 50.;
        z = -240.*v - h-8.*v*v;
        z>12e2? O *= z*(.015-6e-6*z)-8.36 : O;
        O.r += max(32e2 - h - 2.*(z+I.x*v),0.);
    }
    O = min(O/255., 1.);
    O.g = .7*O.r+.3*O.b;

}

