void mainImage( out vec4 fragColor, in vec2 fragCoord )
    {
    float div=3.5;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    uv= vec2(length(uv),atan(-uv.y,uv.x));
    uv*=div;
    vec2 iuv= floor(uv)/div+0.1;
    uv.y= fract(uv.y);


    // Time varying pixel color
    uv.x*=distance(sin(iuv.y*456.),sin(iuv.y*2000545.+iTime*0.01))*5.;
    uv.x+=iTime*0.9;
    float f =uv.y+ (sin(uv.x)*0.5+0.5)*0.25;
    f= smoothstep(0.45,.5,f)-smoothstep(0.5,.55,f);
    vec3 col = vec3(f);

    // Output to screen
    fragColor = vec4(col,1.0);
}
