void drawCircle(vec2 fragCoord, vec2 center, float radius, vec4 color, inout vec4 fragColor)
{
    // Calculate the distance from the fragment to the center of the circle.
    vec2 delta = fragCoord - center;
    float dist = length(delta);

    // If the distance is less than or equal to the radius of the circle,
    // set the fragment color to the specified color.
    if (dist <= radius)
    {
        fragColor = color;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Create a color variable to store the color of each fragment.
    vec4 color = vec4(0.0, 0.0, 0.0, 0.0);

    // Calculate the distance from the fragment to the center of the screen.
    vec2 center = vec2(0.5 * iResolution.x, 0.5 * iResolution.y);
    vec2 delta = fragCoord - center;
    float dist = length(delta);

    // Set the fragment color based on the distance from the center of the screen.
    if (dist <= 150.0)
    {
        // If the distance is less than or equal to 150 pixels,
        // set the fragment color to white.
        color = vec4(1.0, 1.0, 1.0, 1.0);
    }
    else if (dist <= 200.0)
    {
        // If the distance is greater than 150 pixels but less than or equal to 200 pixels,
        // set the fragment color to a light brown color.
        color = vec4(0.78, 0.59, 0.39, 1.0);
    }
    else
    {
        // If the distance is greater than 200 pixels,
        // set the fragment color to black.
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }

    // Draw the owl's eyes.
    vec2 eyeLeft = vec2(center.x - 50.0, center.y - 50.0);
    vec2 eyeRight = vec2(center.x + 50.0, center.y - 50.0);
    drawCircle(fragCoord, eyeLeft, 20.0, vec4(0.0, 0.0, 0.0, 1.0), color);
    drawCircle(fragCoord, eyeRight, 20.0, vec4(0.0, 0.0, 0.0, 1.0), color);

    // Set the final fragment color.
    fragColor = color;
}
