void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//normalize the fragCoord to be between 0 and 1
vec2 uv = fragCoord/iResolution.xy;

//create a gradient from 0 to 1
float gradient = uv.x;

//map the gradient to the rainbow colors (ROYBIV)
vec3 color = vec3(1.0, 0.0, 0.0) * (1.0 - gradient) +
             vec3(1.0, 0.5, 0.0) * (gradient - 0.16) +
             vec3(1.0, 1.0, 0.0) * (gradient - 0.32) +
             vec3(0.0, 1.0, 0.0) * (gradient - 0.48) +
             vec3(0.0, 1.0, 1.0) * (gradient - 0.64) +
             vec3(0.0, 0.0, 1.0) * (gradient - 0.8);

fragColor = vec4(color, 1.0);
}
