precision highp float;
void mainImage( out vec4 o, in vec2 f ){
    vec2 r=iResolution.xy,uv=(2.*f.xy-r)/min(r.x,r.y);
	vec3 p,rd=normalize(vec3(uv,1.)),c;
	float i=0.,j,e=1.,d=2.0,s,ss,t=iTime,a,b;

	for(;i++<64.&&e>.001;){
		p=rd*d;
        p.y+=.1;
        p.z+=t*.3;
        ss=2.;
		for(j=0.;j++<5.;){
            a=cos(p.z+t*.1),b=sin(p.z+t*.1);
            p.xy*=mat2(a,b,-b,a);
            p=abs(mod(p,2.)-1.);
            s=1.9/clamp(dot(p,p),.5,2.8);
            p=p.yzx*s;
            ss*=s;
            p-=vec3(sin(t*.15)*.35+.35,cos(t*.1)*.3+.7,0.);
        }
		c+=.8/(1.+vec3((sin(p.x)*.5+.5)*s,s,s*s));
		d+=e=length(p)/ss-.008;
	}
	s=1.-exp(-i*.1);
	c=c/(1.+c);c=mix(c*s*s,c*c,1.-s);
	o = vec4(c,1.);
}
