void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool auto= true;
    bool tri= false;
    float div= 12.;
    float indice;
    //indice de 0 a 11
    float vel = 0.5;
        if(auto){
    float t;
    if(tri){
    t= abs(mod(iTime * vel*0.5  +0.5,1.)-0.5)*2.;
    }
    else{
    t = fract(iTime * vel);
    }
    indice = floor(mod(t*div,div));
    }
    else{indice=0.;}
    float px = 1./iResolution.x;
    float cantPx = px*(iResolution.x/div);
    vec2 uv = fragCoord.xy/(iResolution.xy);
    //vec2 iuv = floor(fragCoord.xy*div/(iResolution.xy))/div;

    vec3 col = vec3(uv.x);
    col= step(cantPx*(div-indice-1.),1.-col)*step(cantPx*indice,col);
    fragColor = vec4(col,1.0);
}
