// -13 chars thanks to FabriceNeyret2

void mainImage(out vec4 O, vec2 p)
{
    vec3 R = iResolution;

    float t = fract(iTime + texture(iChannel0, p / R.xy).x / 48.),
          r = 1. - t,
          b = 40. * t * r + .5,
          s = (1. - exp(-2. * b)) * (1. + .5 * sin(16. * t) * r);

    p = (12. * p - 6. * R.xz) / R.y;

    O = vec4(length(vec2(p.x * s * s, p.y - b)) < s);
}
