float blob(vec2 pt, vec2 center, float radius){
    pt = pt- center;
    radius*=0.5;
    float dot = dot(pt , vec2(0.0, 1.0));
    float dist = length(pt);
    return step(dist + sin(dot * 90.0 + iTime * 3.0) * radius*0.1, radius);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect =  iResolution.y/iResolution.x;

    vec3 col = vec3(1.0);

    float c = blob(uv * vec2(1.0, aspect), vec2(0.5)  * vec2(1.0, aspect), 0.2);

    // Output to screen
    fragColor = vec4(col * c,1.0);
}
