vec2 triangle_wave(vec2 a){

    vec2 a2 = vec2(1.,0.5),

    a1 = a-a2;

    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);

    vec3 col = vec3(0.);

    float t1 = 1.5;

    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;

    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;

    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;

    vec2 t2 = vec2(0.);

    for(int k = 0; k < 6; k++){

        uv = abs(.5+uv+t2);

        t2 = -triangle_wave(uv-.5)*sign(uv.x-uv.y) ;

        uv = t2-triangle_wave(uv.yx*sign(uv.x-uv.y));

        float c1 = abs(uv.x-uv.y);

        col = col.yzx;

        if(uv.y < uv.x) col = vec3(col.yz,c1);

    }

    fragColor = vec4(min(col,vec3(1.)),1.0);
}

vec2 mainSound(int samp, float time){
  //time += floor(time/2.)/2.;

  float s1 =
      8.,
  m4 =
      pow(2.,fmod(floor(time*s1),3.))
      //pow(2.,fmod((time*2./s1),fmod(time*s1,8.)+8.))
      //fmod(floor(time/s2)*(floor(time)+floor(time*s2)),2.)
  ,
  t=
      time/(m4)
  ,
  m1 =
      fmod(floor(t+m4),s1)
  ,
  m3 =
      fmod(t*s1/2.,s1);
  t /=
      (1.+fmod(floor(t/s1+m3/2.),2.))
  ;
  float a=
      //((1.-sqrt(fract(t/s1))))*.2
      64.*(pow(fract(-t*s1)/1.25,2.))
      //pow(2.-log(fract(t/s1)/s1),2.)
  ,

  nb = time*pow(2.,(m3)/5.+7.);

  return
      abs(.5-vec2(fract(nb*.998),fract(nb)))*a

      //sitar
      //log(abs(a/16.-abs(.5-vec2(fract(nb*.998),fract(nb)))*a))
  ;

}

float fmod(float x,float y){
    return
        floor(mod(x,y))
        //mod(floor(x),y)
        //min(mod(floor(x),y),mod(floor(x),y))
    ;
}
