void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    int numberOfMovedPixels = 0;

    #ifndef ABSOLUTE
    for(int i = -5; i < 6; i++) {
        for(int j = -5; j < 6; j++) {
            vec4 col = texture(iChannel0, (fragCoord + vec2(i, j)) / iResolution.xy);
            if(col.r > 0.5) numberOfMovedPixels++;
        }
    }
    if(numberOfMovedPixels > 80) fragColor = vec4(255, 255, 255, 255);
    else fragColor = vec4(0,0,0,0);
    #else
    fragColor = texture(iChannel0, uv);
    #endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec4 delta = abs(texture(iChannel0, uv) - texture(iChannel1, uv));
    #ifndef ABSOLUTE
    if(delta[0] + delta[1] + delta[2] + delta[3] > 0.5) fragColor = vec4(255,255,255,255);
    else fragColor = vec4(0,0,0,0);
    #else
    fragColor = abs(delta);
    #endif
}
