void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 o_pix_coo_nor = (fragCoord.xy - iResolution.xy*0.5)/iResolution.y;
    vec2 o_mou_coo_nor = (iMouse.xy - iResolution.xy*0.5)/iResolution.y;

    if(iMouse.w == 0.){
        o_mou_coo_nor = vec2(0.01, 0.0);
    }

    float n_freqs = o_mou_coo_nor.x*1000.0;
    n_freqs = 111.; //tmp static
    vec2 o_opcn_big = o_pix_coo_nor*vec2(n_freqs);
    vec2 o_opcn_big_mod = fract(o_opcn_big);
    vec2 o_opcn_big_floor = floor(o_opcn_big);
    // 0.1, 0.1, 0.1, instead of 0.13, 0.16, 0.19...
    vec2 o_pix_coo_nor_subdivfloor = vec2(1.) / o_opcn_big_floor;

    float n_amp = texture(iChannel0,abs(o_pix_coo_nor_subdivfloor)).x; 
    n_amp *=0.5;
    fragColor = vec4(length(o_opcn_big_mod-vec2(0.5)));
    //if(n_amp < abs(o_pix_coo_nor.y)){
    float n_delta = (n_amp*n_freqs) - abs(o_opcn_big.y);
    if(n_delta < 0.){
        vec2 o_mod = fract(vec2(o_opcn_big.x, (n_amp*2.0*n_freqs)));

        float n_length = length(o_mod-vec2(0.5));
        //fragColor = vec4(abs(n_delta/n_freqs));
        fragColor = vec4(n_length*fract(n_delta));
    }


}
