float tricorn(vec2 z, vec2 c)
{
    const int max_iteration = 64;
    for (int it = 0; it < max_iteration; it++)
    {
        float xtemp = z.x*z.x - z.y*z.y + c.x;
        z.y = -2.0 * z.x*z.y + c.y;
        z.x = xtemp;

        if (z.x*z.x + z.y*z.y >= 4.0)
            return float(it) / float(max_iteration) + 0.2;
    }

    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 coord = vec2(3.5 * fragCoord.x / iResolution.x - 1.75, 2.0 * fragCoord.y / iResolution.y - 1.0);
    float col = tricorn(coord, coord * cos(0.5 * iTime));
    fragColor = vec4(0.9 * col, 0.3 * col, 0.4 * col, 1.0);
}
