#define FireSpeed 0.7
#define scaleX 1.3
#define scaleY 0.5

#define colDown vec3(.5,.4,0.1)
#define colUP vec3(1.,.2,.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float normUVX = abs(uv.x-.5);

    float tex = texture(iChannel0,vec2(uv.x*scaleX,uv.y*scaleY - iTime*FireSpeed)).r;

    tex = tex - 0.2 +uv.y*1.2;

    float fire = pow(1.-tex,1.3);

    fire *= (1. -(normUVX * normUVX*4.0))*1.5;

    vec3 col = fire * 8.* mix(colDown,colUP,uv.y);

    fragColor = vec4(col,1.0);
}
