float box( vec3 p, vec3 size )
{
    vec3 d = abs( p ) - size;
    return min( 0., max( d.x, max( d.y, d.z ) ) ) +
        length( max(d, 0.) );
}

vec2 r2( vec2 i, float a )
{
    return vec2( i.x * cos(a) + i.y * sin(a), i.x * -sin(a) + i.y * cos(a) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 rayDirection = normalize( vec3( uv - 0.5, 0.5 ) );
    vec3 position = vec3( 0., 0., -5. );

    rayDirection.xz = r2( rayDirection.xz, iTime*.15 );
    position.xz = r2( position.xz, -iTime*.15 );

    float minDist = 0.;
    int i = 0;
    for( ; i < 50; i++ )
    {
        minDist = box(position, vec3( 1., 0.5, 1. ));
        if( minDist < 0.01 || minDist > 100.0 )
            break;
        rayDirection.xy = r2( rayDirection.xy, minDist );
        float param_cycle = (150.-abs(mod(iTime+120., 600.)-300.f))*0.01;
        rayDirection -= position * param_cycle * 0.1;
        rayDirection = normalize(rayDirection);
        position += minDist * rayDirection;
    }
   vec3 col = vec3( 0., 0., 0. );
    if( minDist < 0.01 )
        col.rgb = vec3( position.xyz )*.5+.5;
    if( minDist > 100. )
        col.rgb = vec3(1.,1.,1.);
    col.rgb *= vec3( float(75-i)/75.);
	fragColor = vec4(col,1.0);
}
