const vec4 BLACK = vec4(0.0);
const vec4 BLUE = vec4(.0, .0, 1, 1);
const float PI = 3.1415926535897932384626433832795;

vec2 pixelize(in vec2 point, float res);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cinematicMargin = 80.0;

    if (length(pixelize(fragCoord.xy - iResolution.xy *.5, 20.0 * sin(iTime))) < 64.0)
    {
        fragColor = BLUE;
        return;
    }


    if (fragCoord.y <= cinematicMargin
     || fragCoord.y > iResolution.y - cinematicMargin)
    {
        fragColor = BLACK;
        return;
    }

    float green = 1. * fragCoord.x / iResolution.x;

    fragColor = BLACK;
}

vec2 pixelize(in vec2 point, float res) {
    return round(point / res) * res;
}
