void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 c = texture(iChannel0,uv + texture(iChannel1, uv*.13 + vec2(iTime * .02)).rg*.05);
    c.r = c.r*(1.0-c.r);
    c.g = c.g*c.g - .3;
    c.b = sqrt(c.b);
    fragColor = c;
}

#define factor .02
#define size 18.0
//mess around with these for fun
#define sliminess 1.5
//it was supposed to be ranged from 0 to 1
//it looks cool when sliminess is 1.5 ngl

#define spinningSpeed 3.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 c;
    vec2 uv = fragCoord/iResolution.xy;
    c = texture(iChannel1, uv);
    vec2 mean = vec2(0);
	for(float i = -size; i<=size; i++)
	{
		for(float j = -size; j<=size; j++)
		{
			if(i*i + j*j <= size * size)mean += texture(iChannel1, uv + vec2(i,j)/iResolution.xy).r * vec2(i,j);
		}
	}

	mean = normalize(mean)*(1.0 - sliminess)+ (mean / pow(2.0*float(size)+1.0,2.0)) * sliminess;

    c = texture(iChannel1, uv + mean * factor)*.99 - vec4(vec3(.005),0);
    if(length(iResolution.xy*.5 + vec2(cos(iTime * spinningSpeed), sin(2.0*iTime * spinningSpeed))* 100.0 - fragCoord) <= 10.0)
    {
        c = vec4(1);
    }

    fragColor = c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
