void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float d = length(uv);

    d = sin(d * 5.0 + iTime)/5.0;
    d = abs(d);

    d = 0.02 / d;

    vec3 col = vec3(d, d, d);

    // Output to screen
    fragColor = vec4(col,d);
}
