void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;



    float eyes_velocity = 1./2.;
    float eyes_amp = abs((0.45 *sin(iTime *eyes_velocity)));
    float eyes_eps = 0.0001;

    float funcUp = sin(uv.x * 3.5 -0.1)*(eyes_amp) + 0.5;
    float funcDown = sin(-uv.x * 3.5 + 0.1)*eyes_amp + 0.5;

    float data1 = (funcDown - uv.y) ;
    float curve1 = step(data1,eyes_eps);

    float data2 = (-funcUp + uv.y);
    float curve2 = step(data2,eyes_eps);


    fragColor = vec4(vec3(curve2 * curve1),1.0);
}
