#define pi 3.14159265
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    uv *= pi;

    vec3 col = vec3(0);

    /*
    col.r += (sin(uv.x-iTime*.7+pi*2.)+1.)/2.5;
    col.g += (sin(uv.x-iTime*.5+(pi*2.)*(1./3.))+1.)/2.5;
    col.b += (sin(uv.x-iTime*.3+(pi*2.)*(2./3.))+1.)/2.5;
    */

    col += (1.+sin(uv.x-iTime*vec3(.7,.5,.3)+pi*2.*vec3(3,1,2)/3.))/2.5;

    col *= smoothstep(pi,.8,abs(uv.y));

    fragColor = vec4(col,1.0);
}
