void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 col0 = vec3(0.000,0.000,1.000);
    vec3 col1 = vec3(1.000,0.969,0.000);

    // Output to screen
    if (fragCoord.y < 64.0){
        vec3 c = mix(col0, col1, texelFetch(iChannel1, ivec2(uv.x * 512., 0), 0).x);
        fragColor = vec4(c, 1.0);
    } else
        fragColor = vec4(texelFetch(iChannel0, ivec2(fragCoord.x, fragCoord.y - 1.0), 0).xyz, 1.0);
}
